import './App.css'

const App = ()=>{
    const [counter,setCounter] = useStat
    const lessHandler = ()=>{
        return counter-1;
    }
    const addHandler = ()=>{
        return counter+1;
    }
    return <div className="container">
        <h1>1</h1>
        <button className="btn" onClick={lessHandler}>-</button>
        <button className="btn" onClick={addHandler}>+</button>
    </div>
}
export default App;
