import LogItem from "./Logitem/LogItem";
import Card from "../UI/Card/Card";
import "./Logs.css";
const Logs = (props) => {
  let filterData = props.logsData.filter(
    (item) => item.date.getFullYear() === 2022
  );
  console.log(filterData);
  const logItemDate = filterData.map((item, index) => (
    <LogItem
      key={item.id}
      date={item.date}
      desc={item.desc}
      time={item.time}
      delLogHandler={() => props.delLogHandler(index)}
    />
  ));
  if (logItemDate.length === 0) {
    logItemDate = <p className="no-logs">没有找到日志!</p>;
  }
  return (
    <Card className="logs">
      {
        logItemDate
        /* {logItemDate.length !== 0 ? (
        logItemDate
      ) : (
        <p className="no-logs">没有找到日志!</p>
      )} */
      }
    </Card>
  );
};
export default Logs;
