  const buttonClick = () => {
    setShowConfirm(true)
  }

  return (
    <Card className="item">
      {showConfirm && <ConfirmModal/>}
      <MyDate date={props.date} />
      <div className="content">
        <h2 className="desc">{props.desc}</h2>
        <div className="time">{props.time}分钟</div>
      </div>
      <div>
        <div className="delItem" onClick={buttonClick}>
          x
        </div>
      </div>
    </Card>
  );
};

export default LogItem;
